import { dbs, racks } from "../Layout/floor4_layout";

function ConnectionLines({selectedDB, connections}){
    if(!selectedDB) return null;

    const db = dbs.find()
    return(
        <svg className="connection-line">
        {connections
            .filter(c => c.dbId === selectedDB)
            .map((c,i) => (
                <line
                    key={i}
                    x1={c.dbX}
                    y1={c.dbY}
                    x2={c.rackX}
                    y2={c.rackY}
                    stroke="orange"
                    strokeWidth="2"
                />
            ))
        }
        </svg>
    )
}

export default ConnectionLines
