const DB_HEIGHT = 60;
const RACK_WIDTH = 90;
const RACK_HEIGHT = 40;

const CABLE_CORRIDOR_x = 1100;

function ConnectionLines({dbs = [], racks = [], selectedDB, connections = []}){
    if(!selectedDB) return null;

    const db = dbs.find(d => d.id === selectedDB);
    if(!db) return null;

    const connectedRacks = connections
