import { ReactFlowProvider, ReactFlow } from "reactflow";
import { nodes, edges } from "./MockData";
import { useState } from "react";
import { allEdges } from "./MockData";

import RackNode from "../Nodes/RackNode";
import DBNode from "../Nodes/DBNode";

function LapMap(){
      const [selectedDB, setSelectedDB] = useState(null);

    const visibleEdges = selectedDB
        ? allEdges.filter(e => e.source === selectedDB)
        : [];

    const onNodeClick = (_, node) => {
        if (node.id.startsWith("DB")) {
        setSelectedDB(prev =>
            prev === node.id ? null : node.id
        );
        }
    };
    const nodeTypes = {
        db: DBNode,
        rack: RackNode
    };

    const nodes = [
        {
            id: "DB1",
            type: "db",
            position: { x: 50, y: 100 },
            data: {
            name: "DB1",
            status: "Active",
            load: 12.5,
            active: true
            }
        },
        {
            id: "A-1",
            type: "rack",
            position: { x: 300, y: 200 },
            data: {
            name: "A-1",
            current: 0.52,
            status: "normal"
            }
        }
    ];

    return(

        <ReactFlowProvider>
            <ReactFlow
                nodes={nodes}
                edges={edges}
                nodeTypes={nodeTypes}
                onNodeClick={onNodeClick}
                fitView
            />
        </ReactFlowProvider>
    )

}

export default LapMap;
