import { useState, useMemo } from "react";
import RackGraph from "./RackGraph";
import RackLog from "./RackLog";
import "../../styles/RackDetailPanel.css";
import RackDataTable from "./RackDataTable";
import { generateFakeTableData } from "../../utils/fakeRackData";
import "../../styles"

const TABS = [
   "電流値",
  "時間別電力量",
  "日別電力量",
  "月別電力量"
];

function RackDetailPanel({rack, onClose}){
    const [activeTab, setActiveTab] = useState("current");
    const [view, setView] = useState("graph");

    const tableData = useMemo(() => {
        return generateFakeTableData(12);
    }, [activeTab]);

    return(
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-box" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <h3>{rack.name}</h3>
                    <button onClick={onClose}>X</button>
                </div>

                <div className="modal-tabs">
                    {/* {TABS.map(tab => (
                        <button
                            key={tab}
                            className={activeTab === tab ? "active" : ""}
                            onClick={() => setActiveTab(tab)}
                        >
                            {tab}
                        </button>
                    ))
                    } */}
                        <button onClick={() => setActiveTab("current")}>電流値</button>
                        <button onClick={() => setActiveTab("hour")}>時間別電力量</button>
                        <button onClick={() => setActiveTab("day")}>日別電力量</button>
                        <button onClick={() => setActiveTab("month")}>月別電力量</button>


                </div>

                <div className="view-switch">
                    <button
                        className={view === "graph" ? "active" : ""}
                        onClick={() => setView("graph")}
                    >
                        グラフ
                    </button>
                    <button
                        className={view === "log" ? "active" : ""}
                        onClick={() => setView("log")}
                    >
                        ログ
                    </button>
                </div>

                <div className="modal-content">
                    {/* {view === "graph" ? (
                        <RackGraph rack={rack} type={activeTab}/>
                    ):(
                        <RackLog rack={rack} type={activeTab}/>
                    )
                    } */}

                    <RackDataTable data={tableData}/>
                </div>
            </div>
        </div>

    )
}
export default RackDetailPanel;
