import { ReactFlowProvider, ReactFlow } from "reactflow";
import { nodes, edges } from "./MockData";
import { rackA, dbNodes} from "./MockData";
import { useState } from "react";
import { allEdges } from "./MockData";

function LapMap(){
      const [selectedDB, setSelectedDB] = useState(null);

    const visibleEdges = selectedDB
        ? allEdges.filter(e => e.source === selectedDB)
        : [];

    const onNodeClick = (_, node) => {
        if (node.id.startsWith("DB")) {
        setSelectedDB(prev =>
            prev === node.id ? null : node.id
        );
        }
    };
    return(

        <ReactFlowProvider>
            <div style={{height: "90vh"}}>
                <ReactFlow
                    nodes={[...rackA, ...dbNodes]}
                    edges={visibleEdges}
                    onNodeClick={onNodeClick}
                    fitView
                />

            </div>
        </ReactFlowProvider>
    )

}

export default LapMap;
