function ConnectionLines({dbs = [], racks = [], selectedDB, connections = []}){
    if(!selectedDB) return null;

    const db = dbs.find(d => d.id === selectedDB);
    if(!db) return null;

    const dbCenter = {
        x: db.x + 120,
        y: db.y +30
    }

    console.log("DB center:", dbCenter);
console.log("Rack:", rack.id, rackCenter);

    return(
        <svg className="connection-line">
        {connections
            .filter(c => c.dbId === selectedDB)
            .map((c, index) =>{
                const rack = racks.find(r => r.id === c.rackId);
                if(!rack) return null;

                const rackCenter = {
                    x: rack.x,
                    y: rack.y + 45
                };

                const midX = (dbCenter.x + rackCenter.x) / 2;

                return (
                    <path
                        key={index}
                        d={`
                            M ${dbCenter.x} ${dbCenter.y}
                            C ${midX} ${dbCenter.y},
                            ${midX} ${rackCenter.y},
                            ${rackCenter.x} ${rackCenter.y}`
                        }
                        stroke="orange"
                        strokeWidth="2"
                    />
                );
            })  
        }
        </svg>
    );
}

export default ConnectionLines
