    // const DB_WIDTH = 120;
const DB_HEIGHT = 60;
const RACK_WIDTH = 90;
const RACK_HEIGHT = 40;

const CABLE_CORRIDOR_x = 1100;

function ConnectionLines({dbs = [], racks = [], selectedDB, connections = []}){
    if(!selectedDB) return null;

    const db = dbs.find(d => d.id === selectedDB);
    if(!db) return null;

    const connectedRacks = connections
                            .filter((c) => c.dbId === selectedDB)
                            .map((c) => racks.find((r) =>r.id === c.rackId))
                            .filter(Boolean);
    if(connectedRacks.length === 0) return null;
    

    const dbOut = {
        x: db.x,
        y: db.y + DB_HEIGHT/2
    };

    const trunkTop = Math.min(...connectedRacks.map(r => r.y));
    const trunkBottom = Math.max(...connectedRacks.map(r => r.y + RACK_HEIGHT));
    const dbStart = {
        x: db.x,
        y: db.y + DB_HEIGHT / 2
    }

    const trunkX = dbStart.x - 120;

    const rackYs = connectedRacks.map((r) => r.y);
    const maxRackY = Math.max(...rackYs);
    // const minRackY = Math.min(...rackYs);


//     console.log("selectedDB:", selectedDB);
// console.log("connections:", connections);
// console.log("dbs:", dbs);
// console.log("racks:", racks);

//             const matchedConnections = connections;
//             console.log("Matched connections:", matchedConnections);

    
    return(
        <svg className="connection-line">
            <path
                d={`
                    M ${dbOut.x} ${dbOut.y}
                    L ${CABLE_CORRIDOR_x} ${dbOut.y},`
                }
                stroke="red"
                strokeWidth="5"
                strokeLinecap="round"
                fill="none"
                opacity="0.9"/>
            {connectedRacks.map((rack, index) => {
                const FAN_OFFSET = 80;

                const offsetY = (index - (connectedRacks.length - 1)/2* FAN_OFFSET);
                const rackEnd = {
                    x: rack.x + RACK_WIDTH/2,
                    y: rack.y,
                };
                const branchStartY = rackEnd.y + offsetY;
                const controlX = trunkX - (trunkX - rackEnd.x) / 2;

            return (
                <path
                    key={index}
                    d={`
                        M ${trunkX} ${branchStartY}
                        C ${controlX} ${branchStartY},
                        ${controlX} ${rackEnd.y}
                        ${rackEnd.x} ${rackEnd.y}`}
                    stroke="red"
                    strokeWidth="2"
                    fill="none"
                    strokeLinecap="round"
                    opacity="0.85"
                    />
                );
            })}
        </svg>


    );

}

export default ConnectionLines
