import { ReactFlowProvider, ReactFlow, Background, Controls } from "reactflow";
import { edges } from "./MockData";
import { useState } from "react";
// import { allEdges } from "./MockData";
import "../../styles/LapMap.css";
import "../../styles/node_connection.css";

import RackNode from "../Nodes/RackNode";
import DBNode from "../Nodes/DBNode";
import ConnectionLines from "../Nodes/ConnectionLines";

    // const nodeTypes = {
    //     db: DBNode,
    //     rack: RackNode
    // };

function LapMap({racks, dbs, connections}){
      const [selectedDB, setSelectedDB] = useState(null);

    const visibleEdges = selectedDB
        ? edges.filter(e => e.source === selectedDB)
        : [];

    const onNodeClick = (_, node) => {
        if (node.id.startsWith("DB")) {
        setSelectedDB(prev =>
            prev === node.id ? null : node.id
        );
        }
    };


    // const nodes = [
    //     {
    //         id: "DB1",
    //         type: "db",
    //         position: { x: 50, y: 100 },
    //         data: {
    //         name: "DB1",
    //         status: "Active",
    //         load: 12.5,
    //         active: true
    //         }
    //     },
    //     {
    //         id: "A-1",
    //         type: "rack",
    //         position: { x: 300, y: 200 },
    //         data: {
    //         name: "A-1",
    //         current: 0.52,
    //         status: "normal"
    //         }
    //     }
    // ];

    return(
        <div className="lap-map">
            { dbs.map(db => (
                <DBNode
                key={db.id}
                data={db}
                selected={selectedDB === db.id}
                onClick={() => 
                    setSelectedDB(selectedDB === db.id ? null : db.id)
                }/>
            ))}

            {racks.map(rack => (
                <RackNode key={rack.id} data={rack}/>
            ))}

            <ConnectionLines
            selectedDB={selectedDB}
            connections={connections}/>
        </div>
    )

}

export default LapMap;
