import { useState } from "react";
import RackGraph from "./RackGraph";
import RackLog from "./RackLog";

const MAIN_TABS = [
  { key: "current", label: "電流値" },
  { key: "hour", label: "時間別電力量" },
  { key: "day", label: "日別電力量" },
  { key: "month", label: "月別電力量" }
];

function RackDetailPanel({rack, logs, onClose}){
    const [mainTab, setMainTab] = useState("current");
    const [subTab, setSubTab] = useState("graph");

    return(
        <div
            style={{
                position: "fixed",
                right: 0,
                top: 0,
                width: 480,
                height: "100vh",
                background: "#fff",
                borderLeft: "1px solid #ccc",
                padding: 12
            }}
        >
            <button onClick={onClose}>X</button>
            <h3>{rack.id}</h3>

            <div>
                {MAIN_TABS.map(t => (
                    <button
                        key={t.key}
                        onClick={() => setMainTab(t.key)}
                    >
                        {t.label}
                    </button>
                ))}
            </div>

            <div style={{marginTop: 8}}>
                <button onClick={() => setSubTab("graph")}>グラフ</button>
                <button onClick={() => setSubTab("log")}>ログ</button>
            </div>

            <div style={{marginTop: 12}}>
                {subTab === "graph" ? (
                        <RackGraph type={mainTab} logs={logs} rack={rack}/>
                    ):(
                        <RackLog logs={logs}/>
                    )
                }
            </div>
        </div>

    )
}
export default RackDetailPanel;
